/*Template Author - Akshata*/#include<bits/stdc++.h>using namespace std;#define int long long const int maxN = 1000001;int spf[maxN]; void pre(){for(int i=0;i<maxN;i++)spf[i]=i;for(int i=2;i*i<maxN;i++)if(spf[i]==i)for(int j=i*i;j<maxN;j+=i)if(spf[j]==j)spf[j]=i;}void inparr(vector<int>&a,int n){for(int i=0;i<n;i++)cin>>a[i];} int binpow(int b,int e){    if(e==0){        return 1;    }    if(e%2){        return b*binpow(b,e-1);    }    int temp = binpow(b,e/2);    return temp*temp;}void solve(){    int n;    cin>>n;    vector<int> v(n);    inparr(v,n);    sort(v.begin(),v.end());    int number = v[0]*v[n-1];    vector<int> vec;    for(int i=2;i*i<=number;i++){        if(number%i==0){            vec.push_back(i);            if(number/i!=i){                vec.push_back(number/i);            }        }    }    sort(vec.begin(),vec.end());    if(v.size()!=vec.size()){        cout<<-1<<endl;        return;    }    for(int i=0;i<v.size();i++){        if(v[i]!=vec[i]){            cout<<-1<<endl;            return;        }    }    cout<<number<<endl;} int32_t main(){ios_base::sync_with_stdio(false);cin.tie(NULL);pre(); // Uncomment for Sieveint t=1;cin>>t;while(t--)solve();return 0;}