/*Template Author - Akshata*/#include<bits/stdc++.h>using namespace std;#define int long long void solve(){    int n, k;    cin >> n >> k;    vector<int> v(n);    for(int i = 0; i < n; i++) cin >> v[i];     set<int> st;    st.insert(1);    int ans = 1;     for(int i = 0; i < n; i++){        vector<int> nv;        bool flag = false;         for(int factor : st){            long long prod = factor * v[i];                        if(prod == k){                flag = true;                break;            }                        if(k % prod == 0){                nv.push_back(prod);            }        }         if(flag){            ans++;            st.clear();            st.insert(1);            if(k % v[i] == 0){                st.insert(v[i]);            }        } else {            for(int val : nv){                st.insert(val);            }        }    }    cout << ans << endl;} int32_t main(){    ios_base::sync_with_stdio(false); cin.tie(NULL);    int t; cin >> t;     while(t--) solve();     return 0;}