/*Template Author - Akshata*/#include<bits/stdc++.h>using namespace std;#define int long long const int maxN = 1000001;int spf[maxN]; void pre(){for(int i=0;i<maxN;i++)spf[i]=i;for(int i=2;i*i<maxN;i++)if(spf[i]==i)for(int j=i*i;j<maxN;j+=i)if(spf[j]==j)spf[j]=i;}void inparr(vector<int>&a,int n){for(int i=0;i<n;i++)cin>>a[i];} void solve(){    int n;    cin>>n;    vector<int> v(n);    inparr(v,n);    int sum = 0;    for(int i=1;i<n;i++){        sum += abs(v[i]-v[i-1]);    }    // cout<<sum<<endl;    int ans = 1e9;    for(int i=0;i<n;i++){        int s = sum;        if(i){            s-=abs(v[i]-v[i-1]);        }        if(i+1<n){            s-=abs(v[i]-v[i+1]);        }        if(i && i+1<n){            s+=abs(v[i+1]-v[i-1]);        }        ans = min(s,ans);    }    cout<<ans<<endl;} int32_t main(){ios_base::sync_with_stdio(false);cin.tie(NULL);// pre(); // Uncomment for Sieveint t=1;cin>>t;while(t--)solve();return 0;}