/*Template Author - Akshata*/#include<bits/stdc++.h>using namespace std;#define int long long const int maxN = 1000001;int spf[maxN]; void pre(){for(int i=0;i<maxN;i++)spf[i]=i;for(int i=2;i*i<maxN;i++)if(spf[i]==i)for(int j=i*i;j<maxN;j+=i)if(spf[j]==j)spf[j]=i;}void inparr(vector<int>&a,int n){for(int i=0;i<n;i++)cin>>a[i];} void solve(){    int n;    cin>>n;    vector<int> a(n),b(n);    inparr(a,n);    inparr(b,n);    sort(a.rbegin(),a.rend());    vector<int> pref(n);    pref[0] = b[0];    for(int i=1;i<n;i++){        pref[i] = b[i] + pref[i-1];    }    int ans = 0;    for(int i=0;i<n;i++){        int level = upper_bound(pref.begin(),pref.end(),i+1)-pref.begin();        ans = max(a[i]*level,ans);    }    cout<<ans<<endl;} int32_t main(){ios_base::sync_with_stdio(false);cin.tie(NULL);// pre(); // Uncomment for Sieveint t=1;cin>>t;while(t--)solve();return 0;}