/*Template Author - Akshata*/#include<bits/stdc++.h>using namespace std;#define int long long const int maxN = 1000001;int spf[maxN]; void pre(){for(int i=0;i<maxN;i++)spf[i]=i;for(int i=2;i*i<maxN;i++)if(spf[i]==i)for(int j=i*i;j<maxN;j+=i)if(spf[j]==j)spf[j]=i;}void inparr(vector<int>&a,int n){for(int i=0;i<n;i++)cin>>a[i];} int binpow(int b,int e,int mod){    if(e==0)    return 1;    if(e%2){        return (binpow(b,e-1,mod)*(b%mod))%mod;    }    int temp = binpow(b,e/2,mod);    return (temp*temp)%mod;}void solve(){    int mod;    cin>>mod;    int ans = 0;    for(int i=1;i<mod;i++){        bool flag = true;        for(int p=1;p<=mod-2;p++){            int x = binpow(i,p,mod)-1;            if(x%mod==0){                flag = false;                break;            }        }        if(flag && (binpow(i,mod-1,mod)-1)%mod == 0){            // cout<<i<<endl;            ans++;        }    }    cout<<ans<<endl;} int32_t main(){ios_base::sync_with_stdio(false);cin.tie(NULL);// pre(); // Uncomment for Sieveint t=1;while(t--)solve();return 0;}